export default {
  data() {
    return {
      type_1: true,
      mType: "1",
      templateList: [],
      logoList: [],
      form: {
        text1: "",
        text2: null,
        text3: null,
        text4: null,
        logoUrl: "",
        color: "#ff0000"
      },
      imgUrl:
        "https://fuss10.elemecdn.com/e/5d/4a731a90594a4af544c0c25941171jpeg.jpeg",
      srcList: [this.imgUrl]
    };
  },
  created: function() {
    this.getTemplateList();
    this.getLogoList();
  },
  methods: {
    preview() {
      this.form.color = this.form.color.substring(1,7);
      console.log("this.form :", this.form);
      // return 0;
      let that = this;
      this.req({
        url: "previewMaterial",
        data: that.form,
        method: "POST"
      }).then(
        res => {
          console.log("res :", res);
          that.imgUrl = res.data;
          that.srcList = [that.imgUrl];
        },
        err => {
          console.log("err :", err);
        }
      );
    },
    uploadSuccess(response, file, fileList) {
      console.log("response :", response);
      this.form.logoUrl = response.data;
    },
    selectChange(value) {
      console.log("value :", value);
      this.imgUrl = value;
      this.srcList = [value];
    },
    addMaterial() {
      let that = this;
      this.req({
        url: "addMaterial",
        data: {
          name: that.form.name,
          imgUrl: that.imgUrl,
          createDate: new Date().getTime()
        },
        method: "POST"
      }).then(
        res => {
          console.log("res :", res);
          this.$router.push({ path: "/material/check" });
        },
        err => {
          console.log("err :", err);
        }
      );
    },
    getTemplateList() {
      let that = this;
      this.req({
        url: "getTemplateList?page=0&limit=0",
        data: {},
        method: "GET"
      }).then(
        res => {
          console.log("res :", res);
          that.templateList = res.data;
        },
        err => {
          console.log("err :", err);
        }
      );
    },
    getLogoList() {
      let that = this;
      this.req({
        url: "getLogoList?page=0&limit=0",
        data: {},
        method: "GET"
      }).then(
        res => {
          console.log("res :", res);
          that.logoList = res.data;
        },
        err => {
          console.log("err :", err);
        }
      );
    },
    typeChange(value) {
      console.log("value :", value);
      if (value == "1") {
        this.type_1 = true;
        this.form.text3 = null;
        this.form.text4 = null;
      } else {
        this.type_1 = false;
      }
    }
  }
};
