$bg: #F2F3F7;
$dark_gray: #889aa4;
$light_gray: #eee;

.login-container {
  min-height: 100%;
  width: 100%;
  background-color: $bg;
  overflow: hidden;

  .login-form {
    position: relative;
    width: 910px;
    max-width: 100%;
    margin: 48px 35px 0;
    margin: 0 auto;
    overflow: hidden;
    display: flex;
    flex-direction: row;
    border-radius: 4px;
    box-shadow: 0px 2px 8px 0px rgba(0,0,0,0.08); 
  }

  .tips {
    font-size: 14px;
    color: #fff;
    margin-bottom: 10px;

    span {
      &:first-of-type {
        margin-right: 16px;
      }
    }
  }

  .svg-container {
    padding: 6px 5px 6px 15px;
    color: $dark_gray;
    vertical-align: middle;
    width: 30px;
    display: inline-block;
  }

  .login_left {
    width: 383px;
    height: 543px;
    float: left;
  }

  .login_right {
    width: 542px;
    height: 543px;
    float: left;
    padding: 95px 92px 92px 112px;
    background-color: #ffffff;
  }

  .pass_login {
    font-size: 18px;
    color: rgba(0,0,0,0.85);
    margin-left: 36px;
    padding-bottom: 16px;
    border-bottom: 2px solid #132343;
  }

  .h_line {
    width: 100%;
    height: 1px;
    margin-top: 17px;
    background-color: #EFEFEF;
  }

  .login_check span {
    font-size: 12px;
    color: rgba(0,0,0,0.85);
  }

  .login_toast {
    display: flex;
    flex-direction: row;
    align-items: center;
    justify-content: center;
    margin-top: 40px;
    margin-bottom: 36px;
    font-size: 16px;
    color: #E23628;
  }

  .title-container {
    position: relative;

    .title {
      font-size: 26px;
      color: $light_gray;
      margin: 0px auto 40px auto;
      text-align: center;
      font-weight: bold;
    }
  }

  .show-pwd {
    position: absolute;
    right: 30px;
    top: 0px;
    font-size: 16px;
    color: $dark_gray;
    cursor: pointer;
    user-select: none;
  }
}
