export default {
  name: 'info',
  data () {
    return {
      major: '', // 专业
      classes: '', // 班级
      total: 100,
      currentPage: 1,
      pagesize: 5,
      loading: false,
      searchContent: '', // 搜索内容
      insertSingleStudentInfoDialog: false,
      batchDialog: false,
      formLabelWidth: '120px',
      studentInfoForm: { // 学生Form
        name: '',
        major: '',
        birthday: '',
        grade: '',
        classes: '',
        photo: ''
      },
      rules: {
        name: [
          { required: true, message: '请输入姓名', trigger: 'blur' }
        ],
        major: [
          { required: true, message: '请选择专业', trigger: 'change' }
        ],
        birthday: [
          { type: 'date', required: true, message: '请选择出生年月', trigger: 'change' }
        ],
        grade: [
          { required: true, message: '请选择年级', trigger: 'change' }
        ],
        classes: [
          { required: true, message: '请选择班级', trigger: 'change' }
        ],
        photo: [
          { required: true, message: '请输入联系方式', trigger: 'blur' }
        ]
      },
      options: [ // 模拟数据
        {
          id: '1',
          majorName: '软件工程'
        },
        {
          id: '2',
          majorName: '建筑工程'
        }
      ],
      data: [{
          label: '东莞中职院',
          children: [{
            label: '计算机专业',
            children: [
              {
                label: '计算机专业1班'
              },
              {
                label: '计算机专业2班'
              }
            ]
          }]
        }, {
          label: '软件专业',
          children: [{
            label: '软件专业2',
            children: [{
              label: '软件专业3'
            }]
          }, {
            label: '软件专业 2-2',
            children: [{
              label: '软件专业 2-2-1'
            }]
          }]
        }
      ],
      defaultProps: {
        children: 'children',
        label: 'label'
      },
      tableData: [
        {
          id: '1',
          name: '张三',
          major: '软件工程',
          birthday: '20年7月',
          grade: '大三',
          classes: '计算机1班',
          number: '99'
        }
      ]

    }
  },
  methods: {
    handleSizeChange(val) {
      console.log(`每页 ${val} 条`);
    },
    handleCurrentChange(val) {
      console.log(`当前页: ${val}`);
    },
    // 添加学生资料
    showInserModal() {
      this.insertSingleStudentInfoDialog = true
    },
    // 批量录入学生资料
    showBatchModal() {
      this.batchDialog = true
    },
    // 专业选择
    majorSelect() {

    },
    // 班级选择
    classesSelect() {

    },
    // 表单专业选择
    formMajorSelect() {

    },
    // 院校专业菜单
    handleNodeClick(data) {
      console.log(data);
    },
    // 树形菜单
    renderContent(h,{node,data,store}) {
      let addElement = arguments[0];
      return addElement('span',{class:"folder-box"},[
        addElement('i',{class:"folder-icon"}),
        addElement('span',"    "),
        addElement('span',arguments[1].node.label)
      ]);
    },
    // 下载模板
    downloadModal() {
      // 加载loading
      this.loading = true
      this.$axios.post('', JSON.stringify({}))
      .then((res) => {
        // 关闭loading
        this.loading = false
        const content = res.data
        const blob = new Blob([content])
        const fileName = '学生资料模板.xls'
        if ('download' in document.createElement('a')) { 
          const link = document.createElement('a')
          link.download = fileName 
          link.style.display = 'none'
          link.href = URL.createObjectURL(blob)
          document.body.appendChild(link)
          link.click() 
          URL.revokeObjectURL(link.href) 
          document.body.removeChild(link)
        } else { 
          navigator.msSaveBlob(blob, fileName)
        }
      }).catch((error) => {
        console.log(error)
        // 关闭loading
        this.loading = false
      })
    },
    // 文件选择
    handleChange(file, fileList) {
      this.fileList = fileList.slice(-3);
    },
    // 前端校验文件上传是否符合条件
    file_info_check(file){
      var ret = "";
      if(file === undefined){
          ret = "";
      }
      var max_file_size = 2 *1024 *1024;
      if(file.size > max_file_size){
          ret = "文件不能大于2Mb";
      }
      var allow_type = ".xlsx";
      var allow_type2 = ".xls";
      if(file.name.indexOf(allow_type) === -1){
        ret = "文件格式要求是.xls/.xlsx";
      } else {
        ret = 'success';
        return ret
      }
      if(file.name.indexOf(allow_type2) === -1){
        ret = "文件格式要求是.xls/.xlsx";
      } else {
        ret = 'success';
        return ret
      }
      return ret;
    },
    uploadImg(fileObj) {
      this.loading = true
      let fileStatus = this.file_info_check(fileObj.file)
      if (fileStatus === 'success') {
        let formData = new FormData();
        formData.append("file", fileObj.file);
        formData.append("id", this.compeSelect);
        formData.append("gameUrl", this.gameUrl);
        this.$axios.post(this.$global.proUrl + '/uploadingFile', formData, {
          headers: {
            "Content-type": "multipart/form-data"
          }
        }).then(res => {
          this.$message({
            message: '提交成功',
            type: 'success'
          })
          this.loading = false
        })
      } else {
        this.$message({
          message: fileStatus,
          type: 'error'
        })
      }
    },
    submitForm(formName) {
      this.$refs[formName].validate((valid) => {
        if (valid) {
          this.insertSingleStudentInfoDialog = false
          this.$refs[formName].resetFields();
        } else {
          return false;
        }
      });
    },
  }
}
