import Cookies from 'js-cookie'
import {Message} from 'element-ui'
import md5 from "js-md5";
export default {
  name: "Login",
  data() {
    const validateUsername = (rule, value, callback) => {
      if (value.length < 3) {
        callback(new Error("请输入有效的用户名"));
      } else {
        callback();
      }
    };
    const validatePassword = (rule, value, callback) => {
      if (value.length < 5) {
        callback(new Error("密码必须大于5位"));
      } else {
        callback();
      }
    };
    return {
      loginForm: {
        username: "",
        password: ""
      },
      loginRules: {
        username: [
          { required: true, trigger: "blur", validator: validateUsername }
        ],
        password: [
          { required: true, trigger: "blur", validator: validatePassword }
        ]
      },
      loading: false,
      passwordType: "password",
      redirect: undefined,
      loginResponse: ''
    };
  },
  watch: {
    $route: {
      handler: function(route) {
        this.redirect = route.query && route.query.redirect;
      },
      immediate: true
    }
  },
  methods: {
    goRegister () {
      localStorage.setItem("hasLogin", true);
      this.$router.push('/register')
    },
    showPwd() {
      if (this.passwordType === "password") {
        this.passwordType = "";
      } else {
        this.passwordType = "password";
      }
      this.$nextTick(() => {
        this.$refs.password.focus();
      });
    },
    // login() {
    //   sessionStorage.setItem('role', '2')
    //   Cookies.set('token', '9172731y28ihasdiasni')
    //   sessionStorage.setItem('token', '9172731y28ihasdiasni')
    //   this.$router.push({ path: '/' })
    // },
    handleLogin() {
      let that = this;
      this.loading = true;
      //数据格式验证
      this.$refs.loginForm.validate(valid => {
        // 格式通过 进行登录逻辑请求
        if (valid) {
          this.$store.dispatch('Login', this.loginForm).then(response => {
            sessionStorage.setItem('token', response.data[0].api_token)
            sessionStorage.setItem('roles', response.data[0].power)
            this.loading = false
            this.$router.push({ path: '/' })
          }).catch(() => {
            this.loading = false
          })
        } else { 
          console.log('error submit!!')
          this.loading = false
          return false
        }
      })
    }
  }
};
