export default {
  name: '',
  data () {
    return {
      gridData: [
        {
          id: '1',
          title: 'A',
          desc: 'A级：90分以上（包含90)'
        }, 
        {
          id: '2', 
          title: 'B',
          desc: 'B级：80-90（包含80）'
        }, 
        {
          id: '3',
          title: 'C',
          desc: 'C级：70-80（包含70）'
        }, 
        {
          id: '4',
          title: 'D',
          desc: 'D级：60-70（包含60）'
        },
        {
          id: '5',
          title: 'E',
          desc: 'E级：60以下（不包含60）'
        }
      ],
      total: 0,
      currentPage: 1,
      pagesize: 8,
      examSearch: '', // 考试搜索
      courseStatus: '全部', // 课程状态
      insertOrModifyModal: false,
      deleteDialog: false,
      modifyDialog: false,
      successDialog: false,
      modifyTitle: 0, // 0 添加 1 修改
      formLabelWidth: '150px',
      count: 3,
      examJsonData: [],
      examForm: {
        examId: '',
        examName: '',
        examImg: '',
        examDesc: '',
        examTime: '',
        examLevel: [
          { integral: '' },
          { integral: '' },
          { integral: '' },
          { integral: '' },
          { integral: '' }
        ]
      },
      rules: {
        examName: [
          { required: true, message: '请输入考试名称', trigger: 'blur' }
        ],
        examImg: [
          { required: true, message: '请上传考试封面', trigger: 'blur' }
        ],
        examTime: [
          { required: true, message: '请输入考试时长', trigger: 'blur' }
        ]
      }
    }
  },
  watch: {
    insertOrModifyModal(val) {
      if (!val) {
        this.requestExamJsonData()
      }
    }
  },
  created() {
    this.requestExamJsonData()
  },
  methods: {
    handleSizeChange(val) {
      
    },
    handleCurrentChange(val) {
      
    },
    // 显示删除Dialog
    showDeleteModal() {
      this.deleteDialog = true
    },
    // 显示添加课程Dialog
    showInserModal(flag) {
      if (flag === 1) {
        this.modifyDialog = true
      } else {
        this.modifyTitle = flag
        this.insertOrModifyModal = true
      }
    },
    searchExam() {
      this.requestSearchExamJsonData()
    },
    // 搜索
    requestSearchExamJsonData() {
      this.$axios.post(this.$global.sApi + '/addtest', JSON.stringify({
        'type': 5,
        'testname': this.examSearch
      })).then(res => {
        this.examJsonData = res.data.data[0]
      })
    },
    // 获取考试列表
    requestExamJsonData() {
      this.$axios.post(this.$global.sApi + '/addtest', JSON.stringify({
        'type': 4,
        'current_page': this.currentPage,
        'perpage': this.pagesize
      })).then(res => {
        this.examJsonData = res.data.data.data
        this.total = res.data.data.total
      })
    },
    // 确认编辑
    modifyCourse() {
      this.modifyTitle = 1
      this.modifyDialog = false
      this.insertOrModifyModal = true
    },
    file_info_check (file) {
      var ret = ''
      if (file === undefined) {
        ret = ''
      }
      // eslint-disable-next-line camelcase
      var max_file_size = 2 * 1024 * 1024
      // eslint-disable-next-line camelcase
      if (file.size > max_file_size) {
        ret = '文件不能大于2Mb'
      }
      var typeJpg = '.jpg'
      var typeJpeg = '.jpeg'
      var typePng = '.png'
      if (file.name.indexOf(typeJpg) === -1) {
        ret = '文件格式要求是.jpg/.jpeg/.png'
      } else {
        ret = 'success'
        return ret
      }
      if (file.name.indexOf(typeJpeg) === -1) {
        ret = '文件格式要求是.jpg/.jpeg/.png'
      } else {
        ret = 'success'
        return ret
      }
      if (file.name.indexOf(typePng) === -1) {
        ret = '文件格式要求是.jpg/.jpeg/.png'
      } else {
        ret = 'success'
        return ret
      }
      return ret
    },
    uploadImg (fileObj) {
      this.loading = true
      let fileStatus = this.file_info_check(fileObj.file)
      if (fileStatus === 'success') {
        let formData = new FormData()
        formData.append('file', fileObj.file)
        this.$axios.post('/upschead', formData, {
          headers: {
            'Content-type': 'multipart/form-data'
          }
        }).then(res => {
          // this.registerForm.imageUrl = res
          this.$message({
            message: '提交成功',
            type: 'success'
          })
          this.examForm.examImg = res.data.path
          this.loading = false
        })
      } else {
        this.$message({
          message: fileStatus,
          type: 'error'
        })
      }
    },
    submitModify(formName) {
      this.$refs[formName].validate((valid) => {
        if (valid) {
          
        }
      })
    },
    submitInsert(formName) {
      this.$refs[formName].validate((valid) => {
        if (valid) {
          this.insertExamApi()
        }
      })
    },
    // 添加考试
    insertExamApi() {
      var integrals = ''
      this.examForm.examLevel.forEach(element => {
        integrals += element.integral + ','
      });
      this.$axios.post(this.$global.sApi + '/addtest', JSON.stringify({
        'type': 1,
        'testname': this.examForm.examName,
        'testheader': this.examForm.examImg,
        'brief': this.examForm.examDesc,
        'testtime': parseInt(this.examForm.examTime),
        'points': integrals.substring(0, integrals.length - 1)
      })).then(res => {
        this.successDialog = true
        this.countDown()
      })
    },
    countDown() {
      const TIME_COUNT = 3;
      if(!this.timer){
        this.count = TIME_COUNT;
        this.timer = setInterval(()=>{
        if(this.count > 0 && this.count <= TIME_COUNT){
          this.count--;
        }else{
          clearInterval(this.timer);
          this.timer = null;
          this.insertOrModifyModal = false
          this.successDialog = false
        }
        },1000)
      }
    }
  }
}
