export default {
  name: '',
  data () {
    return {
      skFirmDialog: false, // 上课企业 Dialog
      skFirmSearch: '', // 上课企业 搜索内容
      courseDialog: false, // 添加课程 Dialog
      courseSearch: '', // 添加课程 搜索内容
      examDialog: false, // 添加考试 Dialog
      examSearch: '', // 添加考试 搜索内容
      medalDialog: false, // 添加勋章 Dialog
      medalSearch: '', // 添加勋章 搜索内容
      gridData: [{
          date: '2016-05-02',
          name: '王小虎',
          address: '上海市普陀区金沙江路 1518 弄'
        }, {
          date: '2016-05-04',
          name: '王小虎',
          address: '上海市普陀区金沙江路 1518 弄'
        }, {
          date: '2016-05-01',
          name: '王小虎',
          address: '上海市普陀区金沙江路 1518 弄'
        }, {
          date: '2016-05-03',
          name: '王小虎',
          address: '上海市普陀区金沙江路 1518 弄'
        }],
      dynamicTags: [
        {
          tag: '邦邦科技',
          type: 'success'
        },
        {
          tag: '邦邦科技',
          type: 'warning'
        },
        {
          tag: '邦邦科技',
          type: 'info'
        }
      ],
      options: [ // 模拟数据
        {
          id: '1',
          majorName: '软件工程'
        },
        {
          id: '2',
          majorName: '建筑工程'
        }
      ],
      formLabelWidth: '120px',
      teachingPlanForm: {
        attendFirm: [], // 上课企业
        teachingPlan: '',  // 请输入教学计划主题
        teachingPlanDesc: [],  // 教学计划内容
        teachingPlanInfo: '', // 教学计划说明
        imageUrl: '', // 教学计划封面
        teachingPlanSchool: '', // 学校
        teachingPlanObj: '', // 上课对象
        teachingPlanMedal: [], // 认证勋章
        teachingPlanComplete: '', // 完成度
        teachingPlanIntegral: '', // 积分
        teachingPlanGrade: '' // 成绩
      },
      rules: {
        attendFirm: [
          { required: true, message: '请选择上课企业', trigger: 'change' }
        ],
        teachingPlan: [
          { required: true, message: '请输入教学计划主题', trigger: 'blur' }
        ],
        teachingPlanDesc: [
          { required: true, message: '请选择教学计划内容', trigger: 'change' }
        ],
        teachingPlanSchool: [
          { required: true, message: '请选择学校', trigger: 'change' }
        ],
        teachingPlanObj: [
          { required: true, message: '请选择上课对象', trigger: 'change' }
        ]
      }
    }
  },
  methods: {
    // 显示企业选择
    showFirmModal() {
      this.skFirmDialog = true
    },
    // 显示添加课程
    showCourseModal() {
      this.courseDialog = true
    },
    // 显示添加考试
    showExamModal() {
      this.examDialog = true
    },
    handleClose(tag) {
      this.dynamicTags.splice(this.dynamicTags.indexOf(tag), 1);
    },
    // 上传图片
    handleAvatarSuccess(res, file) {
        this.teachingPlanForm.imageUrl = URL.createObjectURL(file.raw);
    },
    beforeAvatarUpload(file) {
      const isJPG = file.type === 'image/jpeg';
      const isLt2M = file.size / 1024 / 1024 < 2;

      if (!isJPG) {
        this.$message.error('上传头像图片只能是 JPG 格式!');
      }
      if (!isLt2M) {
        this.$message.error('上传头像图片大小不能超过 2MB!');
      }
      return isJPG && isLt2M;
    },
    handleSelectionFirm(val) {
      this.teachingPlanForm.attendFirm = val;
      console.log(this.teachingPlanForm.attendFirm)
    },
    handleSelectionCourse(val) {
      this.teachingPlanForm.teachingPlanDesc = val;
      console.log(this.teachingPlanForm.teachingPlanDesc)
    },
    handleSelectionExam(val) {
      this.teachingPlanForm.teachingPlanDesc = val;
      console.log(this.teachingPlanForm.teachingPlanDesc)
    },
    handleSelectionMedal(val) {
      this.teachingPlanForm.teachingPlanMedal = val;
      console.log(this.teachingPlanForm.teachingPlanMedal)
    }
  }
}
